import React, {useState} from "react"
import {createPortal} from "react-dom";
import Logo from "./../assets/logo.png"

const Element = document.querySelector("#modal") as HTMLElement;
interface IProps {
    modalHandler(val: boolean): void
}

const AuthModal = (props: IProps) => {


        const [modal, setModal] = useState(false)
        const modalHandler = () => {
            setModal(prev => !prev)
            props.modalHandler(modal)
        }

    return createPortal(
        <div onClick={modalHandler} className="ui dimmer modals visible active">
            <div
                onClick={e => e.stopPropagation()}
                className="ui standard modal visible active"
            >
                <div className="header">Register & Login</div>
                <div className="content">27</div>
                <div className="actions">
                    <div className="ui button">Neutral</div>
                    </div>
            </div>
        </div>
       ,
        Element
    )
}


export default AuthModal
