import React from 'react';
import './App.css';
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Main from "./components/Main";
import Footer from "./components/Footer";
import {BrowserRouter} from "react-router-dom";
import AuthModal from "./components/AuthModal";
import { connect } from 'react-redux';
import {Login} from "./redux/actions";


interface IProps {
    Login(login: string, password: string): Function
}

interface IState {
showModal: boolean
    creator: number
    AuthUser: string,
    AuthPass: string,
    RememberMe: boolean
}



class App extends React.Component<IProps, IState> {
    constructor(props: IProps) {super(props);
        this.state = {
            showModal: false,
            creator: 0,
        AuthUser: "",
            AuthPass: "",
            RememberMe: false

        };}

    ShowModal = (val: boolean, c: number):void => {
        this.setState({showModal: val, creator: c})
    }

    LoginSubmit ()  {
        this.props.Login(this.state.AuthUser, this.state.AuthPass);
    }

    LoginNdRegister = () => {
        if (this.state.creator === 1) {
            return (
                <>
                    <div className="header modal_header">Login</div>
                    <div className="content">
                        <div className="ui input">
                            <input type="text" value={this.state.AuthUser} onChange={(e) => this.setState({AuthUser: e.target.value})} placeholder="Login or Email" className="modal_input"/>
                        </div>
                        <br/>
                        <br/>
                        <div className="ui input">
                            <input type="password" value={this.state.AuthPass} onChange={(e) => this.setState({AuthPass: e.target.value})} placeholder="Password" className="modal_input" />
                        </div>
                        <br/>
                        <br/>
                        <div className="ui input">
                            <br/>
                            <br/>
                            <div className="ui read-only checkbox">
                                <input type="checkbox" onClick={(e) => this.setState({RememberMe: !this.state.RememberMe})}  />
                                    <label>Remember Me</label>
                            </div>
                        </div>
                    </div>
                    <div className="actions">
                        <button className="ui inverted green button" onClick={LoginSubmit}>Submit</button>
                        <button className="ui inverted red button" onClick={ () => this.setState({showModal: false})}>Cancel</button>
                    </div>
                </>
            )
        } else if (this.state.creator === 2) {
            return (
            <>
                <div className="header modal_header">Register</div>
                <div className="content">
                    <div className="ui input email_input">
                        <input type="text" placeholder="Email" className="modal_input" />
                    </div>
                    <br/>
                    <br/>
                    <div className="ui input login_input">
                        <input type="text" placeholder="Login" className="modal_input" />
                    </div>
                </div>
                <div className="content">
                    <div className="ui input email_input">
                        <input type="password" placeholder="Password" className="modal_input" />
                    </div>
                    <br/>
                    <br/>
                    <div className="ui input email_input">
                        <input type="password" placeholder="Re-Password" className="modal_input" />
                    </div>
                    <br/>
                    <br/>
                    <div className="ui input email_input">
                        <input type="text" placeholder="Image" className="modal_input" />
                    </div>
                </div>
                <div className="actions">
                    <button className="ui inverted green button">Submit</button>
                    <button className="ui inverted red button" onClick={ () => this.setState({showModal: false})}>Cancel</button>
                </div>
            </>
            )
        }
    }


  render(): JSX.Element {
    return (
        <BrowserRouter>
            {this.state.showModal === false ?
                <div className="grid-container">
                    <Header modalHandler={this.ShowModal}/>
                    <Sidebar/>
                    <Main/>
                    <Footer/>
                </div>
            :
                <div className="grid-container_modal">
                    <Header modalHandler={this.ShowModal} />
                <AuthModal data={this.LoginNdRegister}  modalHandler={this.ShowModal} />
                </div>
            }
        </BrowserRouter>
    )
  }
}

const mapStateToProps = (state: any) => {
console.log(state);
    return state
}

export default connect(mapStateToProps, {Login})(App);
