import React from 'react';
import './App.css';
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Main from "./components/Main";
import Footer from "./components/Footer";
import {BrowserRouter} from "react-router-dom";
import AuthModal from "./components/AuthModal";


interface IProps {

}

interface IState {
showModal: boolean
    creator: number
}


class App extends React.Component<IProps, IState> {
    constructor(props: IProps) {super(props);   this.state = {showModal: false, creator: 0};}

    ShowModal = (val: boolean, c: number):void => {
        this.setState({showModal: val, creator: c})
    }

    LoginNdRegister = () => {
        if (this.state.creator === 1) {
            return (
                <>
                    <div className="header modal_header">Login</div>
                    <div className="content">
                        <div className="ui input">
                            <input type="text" placeholder="Login or Email" className="modal_input"/>
                        </div>
                        <br/>
                        <br/>
                        <div className="ui input">
                            <input type="password" placeholder="Password" className="modal_input" />
                        </div>
                        <div className="ui input">
                            <label htmlFor="modal_check">Remember Me</label>
                            <input type="checkbox" className="modal_check" />
                        </div>
                    </div>
                    <div className="actions">
                        <div className="ui button">Neutral</div>
                    </div>
                </>
            )
        } else if (this.state.creator === 2) {
            return (
            <>
                <div className="header modal_header">Register</div>
                <div className="content">
                    <div className="ui input email_input">
                        <input type="text" placeholder="Email" />
                    </div>
                    <br/>
                    <br/>
                    <div className="ui input login_input">
                        <input type="text" placeholder="Login" />
                    </div>
                </div>
                <div className="content">
                </div>
                <div className="content">27</div>
                <div className="content">27</div>

                <div className="actions">
                    <div className="ui button">Neutral</div>
                </div>
            </>
            )
        }
    }


  render(): JSX.Element {
    return (
        <BrowserRouter>
            {this.state.showModal === false ?
                <div className="grid-container">
                    <Header modalHandler={this.ShowModal}/>
                    <Sidebar/>
                    <Main/>
                    <Footer/>
                </div>
            :
                <div className="grid-container_modal">
                <Sidebar/>
                <Main/>
                <AuthModal data={this.LoginNdRegister}  modalHandler={this.ShowModal} />
                </div>
            }
        </BrowserRouter>
    )
  }
}

export default App;
